export interface DragEvent {
    type: 'drag' | 'dragend';
    initialX: number;
    initialY: number;
    dragMode: 'brushX' | 'brushY' | 'pan' | null;
    x: number;
    y: number;
    shiftKey: boolean;
    ctrlKey: boolean;
    metaKey: boolean;
}
export declare const useDragEvent: (el: HTMLElement) => DragEvent;
